import { FormControl, FormGroup, Validators } from "@angular/forms";
import * as i0 from "@angular/core";
import * as i1 from "src/app/services/firebase.service";
export class ModalFormComponent {
    constructor(firebaseService) {
        this.firebaseService = firebaseService;
        this.isSignedIn = false;
    }
    ngOnInit() {
        this.form = new FormGroup({
            ModalEmail: new FormControl('', Validators.email),
            ModalPassword: new FormControl('', Validators.required)
        });
    }
    async onSignin(email, password) {
        await this.firebaseService.signIn(email, password);
        if (this.firebaseService.isLoggedIn)
            this.isSignedIn = true;
        alert(`Hey ${email} ,you have just logged in`);
    }
}
